//![main]
#include <iostream>
#include <seqan/align.h>

using namespace seqan;

int main(int argc, char const *argv[])
{
    typedef String<Dna5> TSequence;             // sequence type
    typedef Align<TSequence, ArrayGaps> TAlign;      // align type
//![main]

//![init]
    TSequence seq1 = "";
    TSequence seq2 = "";
    int mat  =  2;
    int mis  = -2;
    int gape = -1;
    int gapo = -2;

    TAlign align;
    resize(rows(align), 2);
    assignSource(row(align, 0), seq1);
    assignSource(row(align, 1), seq2);
//![init]

//![alignment]
    int score = globalAlignment(align, Score<int, Simple>(mat, mis, gape, gapo));
    std::cout << "Score: " << score << std::endl;
    std::cout << align << std::endl;

    return 0;
}

